import React, { Component } from 'react';
import '../App.css';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import DayPicker from 'react-day-picker';
import 'react-day-picker/lib/style.css';
import moment from 'moment';
import Document from './Document.jsx';
import { Progress } from 'semantic-ui-react'
import { Alert } from 'react-alert'
import Navbar from '../components/nav-bar.jsx';
import Navbar2 from '../components/nav-bar2.jsx';

/* PAYMENTS BELOW */
import StripeCheckout from "react-stripe-checkout"
import React1, { useState } from 'react';
/* PAYMENTS ABOVE */

export default class InternshipEmployer extends Component {

  next = (e) => {
    e.preventDefault();
    this.props.increasePercentage();
    this.props.nextStep();
  }

  previous = (e) => {
    e.preventDefault();
    this.props.prevStep();
  }

  /* PAYMENTS BELOW */
  state = {
    pay: false
  }

  handleClick = (e) => {
    this.setState({ pay: true });
  }

  onToken = (token) => {
    fetch('/save-stripe-token', {
      method: 'POST',
      body: JSON.stringify(token),
    }).then(response => {
      response.json().then(data => {
        alert(`We are in business, ${data.email}`);
      });
    });
  }
  /* PAYMENTS ABOVE */



  render() {
    const { values } = this.props;
    return (
      <div class='ask'>
        <Navbar2 />

        {/* <Progress percent={values.percentage} indicating /> */}
        <div class='col left height'>
          <form>
            <p>Enter your title:</p>
            <input
              type='text'
              onChange={this.props.handleChange('employerTitle')}
            />
          </form>
          <br />
          <form>
            <p>Enter your phone number:</p>
            <input
              type='text'
              onChange={this.props.handleChange('phone')}
            />
          </form>
          <br />
          <form>
            <p>Enter your email:</p>
            <input
              type='email'
              onChange={this.props.handleChange('email')}
            />
          </form>
          <br />
          <button class='prev' onClick={this.previous}>Previous </button>

          {/* PAYMENTS BELOW */}
          <br />
          {/* <StripeCheckout 
            stripeKey={process.env.REACT_APP_PUBLIC_KEY}
            token={this.onToken}
            name="Premium Subscription"
            amount={5 * 100}
            billingAddress
            closed = {this.handleClick}
          />
          <br />
          {this.state.pay === true && (<button class='next' onClick={this.next}>Next </button>)} */}
          {/* PAYMENTS ABOVE */}
          <button class='next' onClick={this.next}>Next </button>

        </div>
        <div class='col right'>
          <Document class='doc' values={values}
          />
        </div>
      </div >

    );
  }


}
