import React, { Component } from 'react';
import '../App.css';
import "react-datepicker/dist/react-datepicker.css";
import ArrowBackIosIcon from '@material-ui/icons/ArrowBackIos';

import FormationIntro from './FormationIntro.jsx';
import FormationInfo from './FormationInfo.jsx';

import FormationPDF from './FormationPDF.jsx';

import { Alert } from 'react-alert'


export default class FormationQuestions extends Component {
  state = {
    step: 1,
    percent: 50,
    date: new Date(),
    companyName: '[X]',
    name: '[X]',
    address: '[X]',
    phone: '[X]',
    fax: '[X]',
    companyAddress: '[X]',
    companyCity: '[X]',
    companyCounty: '[X]',
    companyZip: '[X]',
    incorpList: [{ name: "" }]
  }

  nextStep = () => {
    const { step } = this.state
    this.setState({
      step: step + 1
    })
  }

  prevStep = () => {
    const { step } = this.state
    this.setState({
      step: step - 1
    })
  }

  increasePercentage = () => {
    // this.setState((prevState) => ({
    //   percent: prevState.percent >= 100 ? 0 : prevState.percent + 25,
    // }))
    const { percent } = this.state
    this.setState({
      percent: percent + 25
    })
  }


  decreasePercentage = () => {
    this.setState((prevState) => ({
      percent: prevState.percent >= 100 ? 0 : prevState.percent - 25,
    }))
  }


  handleChange = input => event => {
    this.setState({ [input]: event.target.value })
  }

  handleRecitalChange = (e) => {
    let recitalList = [...this.state.recitalList]
    recitalList[e.target.dataset.id][e.target.className] = e.target.value
    this.setState({ recitalList }, () => console.log(this.state.recitalList))
  }

  addRecital = (e) => {
    e.preventDefault();
    this.setState((prevState) => ({
      recitalList: [...prevState.recitalList, { name: "" }],
    }));
  }

  render() {
    const { step } = this.state;
    const {
      percent,
      date,
      companyName,
      name,
      address,
      phone,
      fax,
      companyAddress,
      companyCity,
      companyCounty,
      companyZip,
      incorpList
    } = this.state;
    const values = {
      percent,
      date,
      companyName,
      name,
      address,
      phone,
      fax,
      companyAddress,
      companyCity,
      companyCounty,
      companyZip,
      incorpList

    };

    switch (step) {
      case 1:
        return <FormationIntro
          nextStep={this.nextStep}
          handleChange={this.handleChange}
          increasePercentage={this.increasePercentage}
          decreasePercentage={this.decreasePercentage}
          values={values}
        // doc={EmploymentDocument}
        />
      case 2:
        return <FormationInfo
          nextStep={this.nextStep}
          prevStep={this.prevStep}
          handleChange={this.handleChange}
          increasePercentage={this.increasePercentage}
          decreasePercentage={this.decreasePercentage}
          values={values}
        // doc={EmploymentDocument}
        />
      case 3:
        return <FormationPDF
          prevStep={this.prevStep}
          values={values} />
    }
  }


}
