import React, { Component } from 'react';
import moment from 'moment';

import '../App.css';


export default class MutualNDADocument extends Component {
  constructor(props) {
    super(props);
  }
  render() {
    const { values } = this.props;

    var dateString = values.date.toString();
    var formattedDate = moment(dateString).format("LL");
    // var endDateString = values.endDate.toString();
    // var formattedEndDate = moment(endDateString).format("LL");

    // var signDateString = values.dateSigned.toString();
    // var formattedSignDate = moment(signDateString).format("LL");

    // const cobraCheck = values.cobra === 'Yes'
    // var cobraRequire = ''
    // if (cobraCheck)
    //   cobraRequire = 'Additionally, you will receive a second letter containing information on the Consolidated Omnibus Budget Reconciliation Act (COBRA) continuation of group coverage of your Company provided health benefits.'

    var misc = 'Miscellaneous'
    return (
      <div class='size' >
        <center>
          <h5 class='indoc'>NON-DISCLOSURE AGREEMENT</h5>
          <br />
          <p>This non-disclosure agreement (“Agreement”) is entered into as of {formattedDate}.</p>
          <br />
          <p>This Agreement is made by and between {values.employerName}, a [X] having its principal place of
          business at {values.companyName} (“Company”) and {values.employeeName}, a [X] whose
          mailing address is {values.address} (“Recipient”)
          (collectively the “Parties” or individually each a “Party”).</p>
          <p>RECITALS</p>
        </center>
        <div class='tab'>
          {
            (values.recitalList).map((val, idx) => {
              let recitalId = `recital-${idx}`
              return (
                <div key={idx}>
                  <p>{`${idx + 1}. WHEREAS, ` + (values.recitalList)[idx]['name']}</p>
                </div>

              )
            })
          }
        </div>
        <center>
          <p>AGREEMENT</p>
        </center>
        <p>NOW, THEREFORE, the Parties agree to the following:</p>
        <p>1. Mutual Non-Disclosure. The Company and the Recipient shall be bound by this
        Agreement. The Parties shall be prohibited from sharing or releasing confidential
        information (as defined below). Any release of confidential information must be done
        in compliance and with the terms of the Agreement.</p>
        <p>2. Term. Subject to permitted disclosures in clause 4 and 5 the Parties agree that this
        Agreement shall be in effect for {values.term} from the date of initial disclosure of confidential
        information.</p>
        <p>3. Definition of Confidential Information. “Confidential Information” shall refer to all
        non-public information or material disclosed or provided by the Parties to another
        Party, orally or in writing, that are proprietary to the Company or the Recipient,
        whether or not produced or owned by the Company or the Recipient that are learned
        over the course of the relationship between the Parties, directly or indirectly.
        <p class='tab'>a. {values.confidential}</p>



        </p>


        {/* <h5 className="page-break">{releaseRequire}</h5>
        <p>{releaseRequire2}</p> */}


        <h5 class='indoc'>{misc}</h5>
        <h6 class='tab'>i. INTEGRATION.
          <br /><br />
          <p class='tab'>This offer letter contains the entire understanding between the
          Company and Employee. This letter supersedes any prior representations or
          understandings, written or oral. Notwithstanding this provision, any terms of the
          Employment Agreement agreed to survive termination shall still be in full force
          and effect.</p>
        </h6>
        <h6 class='tab'>ii. GOVERNING LAW.
          <br /><br />
          <p class='tab'>The validity, interpretation, construction and performance of this offer
          letter shall be governed by the laws of the State of
            {values.state} without regard to conflicts of laws principals.</p>
        </h6>
        <h6 class='tab'>iii. ASSIGNMENT, SUCCESSORS AND ASSIGNS.
          <br /><br />
          <p class='tab'>  The rights bestowed upon
          Employee in this agreement are personal to Employee and shall not be effective
          upon any assignment or successors in interest. Any attempted assignment of
          this agreement shall be null and void.
                </p>
        </h6>
        <h6 class='tab'>iv. SEVERABILITY.
          <br /><br />
          <p class='tab'>If one or more provisions of this agreement are deemed void or
          unenforceable such provision shall nevertheless be enforced to the fullest extent
          possible by law and the force and validity of the remainder of the agreement
          shall still be in effect.
          </p>
        </h6>
        <h6 class='tab'> v. COUNTERPARTS.
          <br /><br />
          <p class='tab'>
            This agreement may be executed in counterparts each of
            which shall be deemed an original and which together shall constitute one and
            the same agreement.
                 </p>
        </h6>
        <br />
        <p>[X]</p>
        <p>______________________</p>
        <p>[X]</p>
        <p>______________________</p>

      </div >

    );
  }


}
