import React, { Component } from 'react';
import './App.css';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import DayPicker from 'react-day-picker';
import 'react-day-picker/lib/style.css';
import moment from 'moment';
import Doc from './Document.jsx';
import { Progress } from 'semantic-ui-react'
import { Alert } from 'react-alert'
import { PDFExport } from '@progress/kendo-react-pdf';
import { Page, Text, View, Document, StyleSheet, PDFDownloadLink, PDFViewer } from '@react-pdf/renderer';


const ref = React.createRef();


export default class PDF extends Component {

  // Add this method to the React 
  exportPDF = () => {
    this.resume.save();
  }
  previous = (e) => {
    e.preventDefault();
    this.props.prevStep();

  }


  render() {
    const { values } = this.props;
    var creditRequire = ''
    if (creditCheck)
      creditRequire = values.internName + ' will be receiving academic credit upon successful completion of this internship. The Company agrees to verify successful completion of this internship by ' + values.internName + ' in the event that ' + values.internName + 'â€™s academic institution asks for such verification.'

    return (
      <div class='generate'>
        {/* <Pdf targetRef={ref} filename="internship-document.pdf">
          {({ toPdf }) => <button class='finalize' onClick={toPdf}>Generate Pdf</button>}
        </Pdf>
        <div ref={ref}>
          <Document class='pdfgenerated' values={values} />
        </div> */}
        <center>
          <button class='prev' onClick={this.previous}>Previous </button>
          <button class='finalize' onClick={this.exportPDF}>Download PDF</button>
        </center>
        <PDFExport paperSize="Letter"
          fileName="internship-document.pdf"
          title=""
          subject=""
          keywords=""
          ref={(r) => this.resume = r}>
          <div style={{
            // height: 792,
            // width: 600,
            padding: 'none',
            backgroundColor: 'white',
            margin: 'auto',
            overflowX: 'hidden',
            overflowY: 'hidden'
          }}>
            <ol class='info'>
              <li>
                Internship Description. [JOB RESPONSIBILITIES]
              <ol class='details'>
                  <li>
                    <span class='word'>Educational Environment.</span> This internship is for the educational benefit of <span class='fill'>{values.internName}</span>. {creditRequire} <span class='fill'>{values.internName}</span> will be the primary beneficiary of this internship.
                </li>
                  <li>
                    <span class='word'>No Expectation of Employment.</span> By signing this agreement <span class='fill'>{values.internName}</span> acknowledges and
                  agrees that this internship is for a fixed duration and comes with no promises or
                  expectations of employment beyond the internship.
                </li>
                </ol>
              </li>
              <br />
              <li>
                Directives, Policies and Procedures. Employment is conditioned upon continued adherence to the directives, policies and procedures of the Company.
                As an employee you are expected to comply with the directives of your Manager as well as the policies and procedures that the
                Company puts in place.  Policies and procedures can include, but are not limited to, the contents of an employee handbook or
                employee training manual.  These policies and procedures may be modified at any time.
            </li>
            </ol>
          </div>
        </PDFExport>


      </div>
    );
  }


}
