import React, { Component } from 'react';
import '../App.css';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import moment from 'moment';
import { Progress } from 'semantic-ui-react'
import DisclosureNDADocument from './DisclosureNDADocument.jsx';


export default class DisclosureNDAInfo2 extends Component {
  next = (e) => {
    e.preventDefault();
    this.props.increasePercentage();
    this.props.nextStep();

  }
  previous = (e) => {
    e.preventDefault();
    this.props.prevStep();
    this.props.decreasePercentage();

  }

  render() {
    const { values } = this.props;
    return (
      <div class='ask'>
        {/* <Progress percent={values.percentage} indicating /> */}
        <div class='col height'>


          <form>
            <p>How long do you want the NDA to last?</p>
            <input
              type='text'
              onChange={this.props.handleChange('term')}
            />
          </form>
          <br />

          <form>
            <p>Enter the state: </p>
            <input
              type='text'
              onChange={this.props.handleChange('state')}
            />
          </form>
          <br />

          <form>
            <p>Do you want to permit disclosure to third parties in order to carry out the purpose of the agreement?</p>
            <input
              type='radio' id="yes" name="third" value="Yes"
              onChange={this.props.handleChange('partyCheck')}
            />
            <label for="yes"> Yes </label><br />
            <input
              type='radio' id="no" name="third" value="No"
              onChange={this.props.handleChange('partyCheck')}
            />
            <label for="no"> No </label><br />

          </form>

          {values.partyCheck === 'Yes' && (
            <div>
              <form>
                <p>Please list the third parties:</p>
                <input
                  type='text'
                  onChange={this.props.handleChange('parties')}
                />
              </form>
              <br />
            </div>
          )}




          <button class='prev' onClick={this.previous}>Previous </button>
          <button class='next' onClick={this.next}>Next </button>
        </div>
        <div class='col right'>
          <DisclosureNDADocument class='doc' values={values} />
        </div>

      </div >

    );
  }


}
