        </PDFExport> */}
        <Document>
          <Doc class='pdfgenerated' values={values} />
        </Document>
        <PDFDownloadLink document={<MyDoc />} fileName="somename.pdf">
          {({ blob, url, loading, error }) => (loading ? 'Loading document...' : 'Download now!')}
        </PDFDownloadLink>


      </div>
    );
  }


}
