import React, { Component } from 'react';
import '../App.css';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import moment from 'moment';
import { Progress } from 'semantic-ui-react'
// import DisciplinaryDocument from './DisciplinaryDocument.jsx';


export default class TerminationInfo2 extends Component {
  next = (e) => {
    e.preventDefault();
    this.props.increasePercentage();
    this.props.nextStep();

  }
  previous = (e) => {
    e.preventDefault();
    this.props.prevStep();
    this.props.decreasePercentage();

  }

  render() {
    const { values } = this.props;
    return (
      <div class='ask'>
        {/* <Progress percent={values.percentage} indicating /> */}

        <div class='col height'>

          <form>
            <p>Enter the date of the last payment:</p>
            <input
              type='date'
              onChange={this.props.handleChange('endDate')}
            />
          </form>
          <br />
          <br />

          <form>
            <p>Enter any company property that has to be returned:</p>
            <input
              type='date'
              onChange={this.props.handleChange('endDate')}
            />
          </form>
          <br />
          <br />

          {values.forCause === 'Mutual' && (
            <div>
              <form>
                <p>Please write your conception of the termination of the relationship:</p>
                <input
                  type='text'
                  onChange={this.props.handleChange('causee')}
                />
              </form>
              <br />
            </div>
          )}

          <form>
            <p>Are you willing to give a reference or only a confirmation of employment?</p>
            <input
              type='radio' id="reference" name="ref" value="Reference"
              onChange={this.props.handleChange('reference')}
            />
            <label for="reference"> Reference </label><br />
            <input
              type='radio' id="confirmation" name="ref" value="Confirmation"
              onChange={this.props.handleChange('reference')}
            />
            <label for="no"> Confirmation </label><br />
          </form>
          <br />

          <form>
            <p>Enter your contact number:</p>
            <input
              type='text'
              onChange={this.props.handleChange('contact')}
            />
          </form>
          <br />








          {/* <form>
            <p>What is the employee being warned for?</p>
            <input
              type='text'
              onChange={this.props.handleChange('warning')}
            />
          </form>
          <br />

          <form>
            <p>What date was their employment agreement signed</p>
            <input
              type='date'
              onChange={this.props.handleChange('dateSigned')}
            />
          </form>
          <br /> */}
          <form>
            <p>Is this a restaurant?</p>
            <input
              type='radio' id="yes" name="res" value="Yes"
              onChange={this.props.handleChange('restaurant')}
            />
            <label for="yes"> Yes </label><br />
            <input
              type='radio' id="no" name="res" value="No"
              onChange={this.props.handleChange('restaurant')}
            />
            <label for="no"> No </label><br />

          </form>
          <br />

          <button class='prev' onClick={this.previous}>Previous </button>
          <button class='next' onClick={this.next}>Next </button>
        </div>
        <div class='col right'>
          {/* <DisciplinaryDocument class='doc' values={values} /> */}
        </div>

      </div>

    );
  }


}
