import React, { Component } from 'react';
import '../App.css';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import DayPicker from 'react-day-picker';
import 'react-day-picker/lib/style.css';
import moment from 'moment';
import ArrowBackIosIcon from '@material-ui/icons/ArrowBackIos';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  HashRouter
} from "react-router-dom";

import { Progress } from 'semantic-ui-react'
import { Alert } from 'react-alert'
import WaiverShareholderDocument from './WaiverShareholderDocument.jsx';
import Navbar from '../components/nav-bar.jsx';




export default class WaiverShareholdersIntro extends Component {

  next = (e) => {
    e.preventDefault();
    this.props.increasePercentage();
    this.props.nextStep();
  }

  render() {
    const { values } = this.props;
    return (
      <div class='ask'>
        <Navbar />

        <div class="back-button" style={{ paddingLeft: "20px", marginLeft: "20px" }}>
          <Link to="/Governance" style={{ color: "black", textDecoration: "none" }}> <ArrowBackIosIcon className="back-button" /></Link>
        </div>
        {/* <Progress percent={values.percentage} indicating /> */}
        <div class='col left height'>

          <form>
            <p>Enter the company's name:</p>
            <input
              type='text'
              onChange={this.props.handleChange('companyName')}
            />
          </form>
          <br />

          <form>
            <p>Enter the state:</p>
            <input
              type='text'
              onChange={this.props.handleChange('state')}
            />
          </form>
          <br />

          <form>
            <p>Enter the shareholder's name:</p>
            <input
              type='text'
              onChange={this.props.handleChange('name')}
            />
          </form>
          <br />

          <form>
            <p>Is this an annual or special meeting?</p>
            <input
              type='radio' id="annual" name="meet" value="Annual"
              onChange={this.props.handleChange('type')}
            />
            <label for="annual"> Annual Meeting</label><br />
            <input
              type='radio' id="special" name="meet" value="Special"
              onChange={this.props.handleChange('type')}
            />
            <label for="no"> Special Meeting </label><br />

          </form>
          <br />





          <br />

          <button class='next' onClick={this.next}>Next </button>
        </div>
        <div class='col right'>
          <WaiverShareholderDocument class='doc' values={values} />
        </div>
      </div >

    );
  }


}
