import React, { Component } from 'react';
import './App.css';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import moment from 'moment';
import Document from './Document.jsx';


export default class Questions extends Component {
  // state = {
  //   startDate: new Date()
  // };

  next = (e) => {
    e.preventDefault();
    this.props.nextStep();
  }

  render() {
    const { values } = this.props;
    return (
      <div class='ask'>
        <p> Enter the date: </p>
        <DatePicker
          // selected={this.state.startDate}
          onChange={this.props.handleDateChange('date')}
        />
        <form>
          <p>Enter your name:</p>
          <input
            type='text'
            onChange={this.props.handleChange('employeeName')}
          />
        </form>
        <form>
          <p>Enter your address:</p>
          <input
            type='text'
            onChange={this.props.handleChange('address')}
          />
        </form>

        <form>
          <p>Enter your intern's name:</p>
          <input
            type='text'
            onChange={this.props.handleChange('internName')}
          />
        </form>

        <form>
          <p>Enter company's name:</p>
          <input
            type='text'
            onChange={this.props.handleChange('companyName')}
          />
        </form>
        <button onClick={this.next}>Next </button>
      </div>

    );
  }


}
