import React, { Component } from 'react';
import '../App.css';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import moment from 'moment';
import { Progress } from 'semantic-ui-react'
import BoardMinutesDocument from './BoardMinutesDocument.jsx';
import Navbar from '../components/nav-bar.jsx';



export default class BoardMinutesInfo extends Component {
  next = (e) => {
    e.preventDefault();
    this.props.increasePercentage();
    this.props.nextStep();

  }
  previous = (e) => {
    e.preventDefault();
    this.props.prevStep();
    this.props.decreasePercentage();

  }

  render() {
    const { values } = this.props;
    return (
      <div class='ask'>
        <Navbar />

        {/* <Progress percent={values.percentage} indicating /> */}
        <div class='col height'>


          <form>
            <p>Were all the directors present in person?</p>
            <input
              type='radio' id="yes" name="pres" value="Yes"
              onChange={this.props.handleChange('present')}
            />
            <label for="yes"> Yes </label><br />
            <input
              type='radio' id="no" name="pres" value="No"
              onChange={this.props.handleChange('present')}
            />
            <label for="no"> No </label><br />

          </form>



          <form onChange={this.props.handleDirectorChange}>
            <div class='listExp'>
              <p>Enter the directors present at the meeting: </p>
            </div>
            <div class='expButton'>
              <button class='add' onClick={this.props.addDirector}>+</button>
            </div>
            {
              (values.directors).map((val, idx) => {
                let nameId = `name-${idx}`, addressId = `address-${idx}`
                return (
                  <div key={idx}>
                    <label htmlFor={nameId}>Name of Director </label>
                    <input
                      type="text"
                      name={nameId}
                      data-id={idx}
                      id={nameId}
                      value={(values.directors)[idx].name}
                      className="name"
                    />
                    <br />
                    <label htmlFor={addressId}>Address of Director</label>
                    <input
                      type="text"
                      name={addressId}
                      data-id={idx}
                      id={addressId}
                      value={(values.directors)[idx].address}
                      className="address"
                    />
                    <br /><br />
                    {" "}
                  </div>

                )
              })
            }
          </form>
          <br />

          <form onChange={this.props.handleRecitalChange}>
            <div class='listExp'>
              <p>What is the reasoning for entering into <br />this agreement? Please note that each <br /> reason should be a separate recital.</p>
            </div>
            <div class='expButton'>
              <button class='add' onClick={this.props.addRecital}>+</button>
            </div>
            <br />
            <div class='listExp'>
              {
                (values.recitalList).map((val, idx) => {
                  let recitalId = `recital-${idx}`
                  return (
                    <div key={idx}>
                      <label htmlFor={recitalId}>{`Recital #${idx + 1}`}</label>
                      <input
                        type="text"
                        name={recitalId}
                        data-id={idx}
                        id={recitalId}
                        value={(values.recitalList)[idx].name}
                        className="name"
                      />
                      {" "}
                    </div>

                  )
                })
              }
            </div>
          </form>

          <form>
            <p>What is the Company's definition of <br />Confidential Information?</p>
            <input
              type='text'
              onChange={this.props.handleChange('confidential')}
            />
          </form>
          <br />

          <button class='prev' onClick={this.previous}>Previous </button>
          <button class='next' onClick={this.next}>Next </button>
        </div>
        <div class='col right'>
          <BoardMinutesDocument class='doc' values={values} />
        </div>

      </div >

    );
  }


}
